import { Bar } from "react-chartjs-2";
import { useState } from "react";

const IncExpChart = ({ addInc, addAmount }) => {
  const [chartData, setChartData] = useState({
    labels: [addInc],
    datasets: [
      {
        label: "Income",
        data: [addAmount],
        backgroundColor: ["red"],
      },
    ],
  });
  return (
    <div>
      <Bar data={chartData} width={100} height={50} />
    </div>
  );
};
export default IncExpChart;
