import { Pie } from "react-chartjs-2";
import { useState } from "react";

const IncExpChart = ({ addInc }) => {
  return (
    <div>
      <Pie
        data={addInc}
        options={{
          plugins: {
            title: {
              display: true,
              text: "Cryptocurrency prices",
            },
            legend: {
              display: true,
              position: "bottom",
            },
          },
        }}
      />
    </div>
  );
};
export default IncExpChart;
