import { useState } from "react";

const Search = () => {
  const [term, setterm] = useState("");
  const searchItem = () => {
    items.filter((i) => i.name.toLowerCase().includes(term.toLowerCase()));
  };
  return (
    <div>
      <input
        type="search"
        placeholder="search Item"
        value="name"
        onChange={(e) => setterm(e.target.value)}
      />

      {searchItem}
    </div>
  );
};
export default Search;
