import "./AddIncome.css";
import { useState } from "react";
const AddIncome = () => {
  const [inc, setinc] = useState([
    {
      name: "salary",
      amount: 500,
    },
    {
      name: "bonus",
      amount: 50,
    },
  ]);
  const [addInc, setAddInc] = useState("");

  //   const Tasks = () => {
