import "./AddExpense.css";
const AddExpense = () => {
  const exp = [
    { name: "rent", amount: 500 },
    { name: "grocery", amount: 50 },
  ];
  const [AddExp, setAddExp] = useState("");
  const [AddExpAmount, setAddExpAmount] = useState("");

  return (
    <div className="AddExpense-container">
      <input type="text" placeholder=" Enter Expense" value={AddExp} />
      <br />
      <input
        type="number"
        placeholder=" Enter Expense Amount"
        value={AddExpAmount}
        required="true"
        onChange={(e) => setAddExpAmount(e.target.value)}
      />
      <br />
      <button type="submit">Submit</button>
      <br />
      <h2>Expense Transactions</h2>
      {exp.map((ex) => (
        <div className="exp-list">
          <div>{ex.name}</div>
          <div>Â£{ex.amount}</div>
          <button>Del</button>
        </div>
      ))}
    </div>
  );
};
export default AddExpense;
