import "./App.css";
import { useState } from "react";
import AddTask from "./AddTask";
import Search from "./Search";

import Items from "./Items";

const App = () => {
  const [items, setItems] = useState([
    { name: "Milk" },
    { name: "Yogurt" },
    { name: "choc" },
  ]);

  const deleteItem = (item) => {
    setItems(items.filter((fil) => fil.name !== item.name));
  };

  return (
    <div
      className="Cart"
      style={{
        width: 500,
        height: 500,
        color: "blue",
        border: "solid 2px",
      }}
    >
      <Search items={items} />
      <Items items={items} setItems={setItems} ondelete={deleteItem} />
      <AddTask items={items} setItems={setItems} />
    </div>
  );
};

export default App;
