import "./AddExpense.css";
import { useState } from "react";
const AddExpense = () => {
  const [AddExp, setAddExp] = useState("");
  const [AddExpAmount, setAddExpAmount] = useState("");
  const expense = () => {
    setExp([...exp, { name: AddExp, amount: AddExpAmount }]);
    setAddExp("");
    setAddExpAmount("");
  };

  return (
    <div className="AddExpense-container">
      <input
        type="text"
        placeholder=" Enter Expense"
        value={AddExp}
        onChange={(e) => setAddExp(e.target.value)}
      />
      <br />
      <input
        type="number"
        placeholder=" Enter Expense Amount"
        value={AddExpAmount}
        required="required"
        onChange={(e) => setAddExpAmount(e.target.value)}
      />
      <br />
      <button type="submit" onClick={expense}>
        Submit
      </button>
      <br />
      <h2>Expense Transactions</h2>
      {exp.map((ex) => (
        <div className="exp-list">
          <div>{ex.name}</div>
          <div>Â£{ex.amount}</div>
          <button
            onClick={() =>
              setExp(
                exp.filter(
                  (expf) => expf.name !== ex.name && expf.amount !== ex.amount
                )
              )
            }
          >
            Del
          </button>
        </div>
      ))}
    </div>
  );
};
export default AddExpense;
