import "./App.css";
import Balance from "./Balance.js";
import IncExpView from "./IncExpView.js";
import AddIncome from "./AddIncome.js";
import AddExpense from "./AddExpense";
import { useState } from "react";

const App = () => {
  const [inc, setinc] = useState([
    // {
    //   name: "salary",
    //   amount: 500,
    // },
    // {
    //   name: "bonus",
    //   amount: 50,
    // },
  ]);
  const [exp, setExp] = useState([
    // {
    //   name: "rent",
    //   amount: 500,
    // },
    // {
    //   name: "grocery",
    //   amount: 50,
    // },
  ]);
  const totalIncome = inc.reduce(
    (totalinc, total) => (totalinc = totalinc + Number(total.amount)),
    0
  );
  return (
    <div className="container">
      <div className="inner-container">
        <h1 className="title">BUDGET TRACKER</h1>

        <Balance />

        <IncExpView
          inc={inc}
          exp={exp}
          totalIncome={totalIncome}
          totalExpense={totalExpense}
        />
        <AddIncome inc={inc} setinc={setinc} />
        <AddExpense exp={exp} setExp={setExp} />
      </div>
    </div>
  );
};

export default App;
