import { Pie } from "react-chartjs-2";
import { useState } from "react";

const IncExpChart = ({ addInc, addAmount }) => {
  const [chartData, setChartData] = useState({});
  setChartData({
    labels: addAmount,
    datasets: [{ data: addInc }],
  });
  return (
    <div>
      <Pie
        data={chartData}
        options={{
          title: {
            display: true,
            text: "Average Rainfall per month",
            fontSize: 20,
          },
          legend: {
            display: true,
            position: "right",
          },
        }}
      />
    </div>
  );
};
export default IncExpChart;
