import "./AddIncome.css";
import { useState } from "react";
import { MdDelete } from "react-icons/md";
import { Scrollbars } from "react-custom-scrollbars";

const AddIncome = ({ inc, setinc }) => {
  const [addInc, setAddInc] = useState("");
  const [addAmount, setAddAmount] = useState("");

  const add = () => {
    setinc([...inc, { name: addInc, amount: addAmount }]);
    setAddInc("");
    setAddAmount("");
  };
  return (
    <div className="AddIncome-container">
      <input
        type="text"
        placeholder=" Enter Income"
        value={addInc}
        onChange={(e) => setAddInc(e.target.value)}
      />

      <br />
      <input
        type="number"
        placeholder=" Enter Income Amount"
        value={addAmount}
        onChange={(e) => setAddAmount(e.target.value)}
      />
      <br />
      <button type="submit" onClick={add}>
        Submit
      </button>
      <br />
      <h2>Income Transactions</h2>
      <Scrollbars
        style={{ height: 200 }}
        renderThumbVertical={({ style, ...props }) => (
          <div
            {...props}
            style={{
              ...style,
              backgroundColor: "#fff",
              width: "4px",
              opacity: "1",
            }}
          />
        )}
      >
        {inc.map((i) => (
          <div className="inc-list">
            <div>{i.name}</div>

            <div>Â£{i.amount}</div>
            <button
              onClick={() => {
                setinc(
                  inc.filter(
                    (incf) => incf.name !== i.name || incf.amount !== i.amount
                  )
                );
              }}
            >
              <MdDelete />
            </button>
          </div>
        ))}
      </Scrollbars>
    </div>
  );
};
export default AddIncome;
