import { Pie } from "react-chartjs-2";

const state = {
  labels: ["Income"],
  datasets: [
    {
      label: "Rainfall",
      backgroundColor: ["red"],

      data: ["Amount"],
    },
  ],
};
const IncExpChart = () => {
  return (
    <div>
      <Pie
        data={state}
        options={{
          title: {
            display: true,
            text: "Average Rainfall per month",
            fontSize: 20,
          },
          legend: {
            display: true,
            position: "right",
          },
        }}
      />
    </div>
  );
};
export default IncExpChart;
